// define quadrilateral's corners
List<IntPoint> corners = new List<IntPoint>( );
corners.Add( new IntPoint(  99,  99 ) );
corners.Add( new IntPoint( 156,  79 ) );
corners.Add( new IntPoint( 184, 126 ) );
